$(document).ready(function () {
    $("#tide,#tide2").slideUp("slow");
    $("#sal").click(function () {
      $("#tide,#tide2").slideToggle("slow");
    });
  });
  $(document).ready(function () {
    $().ready(function () {
      $("#img1,#img2,#img3,.description").hide();

    });
    $("#hide").click(function () {
      $("#img1,#img2,#img3,.description").hide();
   
    });
    $("#show").click(function () {
      $("#img1,#img2,#img3").show();

    });
    $("#img2").click(function () {
      $(".description").fadeTo("slow", 1);
    });
  });
  $(document).ready(function () {
    $(".header_consult").mousemove(function (event) {
      $(".header_consult").animate({ height: "250px" });
    });
  });